define('CLASSIFIEDS_CAT_WATCH_TABLE',		$table_prefix . 'classifieds_cat_watch');
define('CLASSIFIEDS_IMAGES_TABLE',		$table_prefix . 'classifieds_images');
define('CLASSIFIEDS_RULES_TABLE',		$table_prefix . 'classifieds_rules');
define('CLASSIFIEDS_CURRENCY_TABLE',		$table_prefix . 'classifieds_currency');
define('CLASSIFIEDS_PREFIXES_TABLE',		$table_prefix . 'classifieds_prefixes');
